class Quill {
    constructor(selector, options) {
        this.container = document.querySelector(selector);
        this.container.innerHTML = `<div class="ql-container ql-snow"><div class="ql-editor" contenteditable="true">${this.container.innerHTML}</div></div>`;
        this.root = this.container.querySelector('.ql-editor');
        this.options = options;
        this.initToolbar();
    }
    initToolbar() {
        const toolbar = document.createElement('div');
        toolbar.className = 'ql-toolbar ql-snow';
        toolbar.innerHTML = `
            <span class="ql-formats">
                <button class="ql-button ql-bold">B</button>
                <button class="ql-button ql-italic">I</button>
            </span>
        `;
        this.container.prepend(toolbar);
        toolbar.querySelector('.ql-bold').addEventListener('click', () => this.format('bold'));
        toolbar.querySelector('.ql-italic').addEventListener('click', () => this.format('italic'));
    }
    format(type) {
        document.execCommand(type, false, null);
    }
}